const express = require("express");
const cors = require("cors");

const app = express();
const port = 3000;

const privateKey =
  process.env.EXTENSION_PRIVATE_KEY ||
  ``;

app.use(cors());

app.get("/api/privateKey", (_req, res) => {
  res.json({ privateKey });
});

app.listen(port, () => {
  console.log(`Server listening at http://localhost:${port}`);
});
